use std::collections::HashSet;

fn solve0(input: &str, distinct: usize) -> Option<usize> {
    input
        .chars()
        .collect::<Vec<char>>()
        .windows(distinct)
        .enumerate()
        .filter_map(|(i, w)| {
            if w.len() == w.iter().collect::<HashSet<_>>().len() {
                Some(i + distinct)
            } else {
                None
            }
        })
        .next()
}

pub fn solve(input: &str) -> Option<usize> {
    solve0(input, 4)
}

pub fn solve2(input: &str) -> Option<usize> {
    solve0(input, 14)
}

pub const INPUT: &str = "";
